local function InitData(self,showType)
    if showType == MigrateShowItem.ServerName then
        self.title:SetText(Localization:GetString("100031"))
    elseif showType == MigrateShowItem.ServerOpenTime then
        self.title:SetText(Localization:GetString("100031"))
    elseif showType == MigrateShowItem.ServerSeason then
