local function SetData(self,str,isName,color)
    if isName then
        self.num:SetText("")
        self.name:SetText(str)
        if color~=nil then
            self.name:SetColor(color) 
        else
            self.name:SetColor(Color.New(0.72,0.4,0.19,1))
        end
    else
        self.name:SetText("")
        self.num:SetText(str)
        if color~=nil then
            self.num:SetColor(color)
        else
            self.num:SetColor(Color.New(0.72,0.4,0.19,1))
        end
    end
end


