local UICommonSingleMsgBarView = BaseClass("UICommonSingleMsgBarView",UIBaseView)
local base = UIBaseView
local Localization = CS.GameEntry.Localization

local animator_path = "Panel/msgItem/anim"
local tipTxt_path = "Panel/msgItem/anim/Text"

local function OnCreate(self)
    base.OnCreate(self)
    
    self.animatorN = self:AddComponent(UIAnimator, animator_path)
    self.tipTxtN = self:AddComponent(UIText, tipTxt_path)
    if self.cacheMsg then
        self:ShowStrTip(self.cacheMsg)
    end
end

local function OnDestroy(self)
    base.OnDestroy(self)
end

local function ShowStrTip(self, strMsg)
    if self.tipTxtN then
        self.tipTxtN:SetText(strMsg)
        self.animatorN:Play("ShowSingleMsg", 0, 0)
    else
        self.cacheMsg = strMsg
    end
end

UICommonSingleMsgBarView.OnCreate = OnCreate
UICommonSingleMsgBarView.OnDestroy = OnDestroy

UICommonSingleMsgBarView.ShowStrTip = ShowStrTip

return UICommonSingleMsgBarView