local UICrossServerListCtrl = BaseClass("UICrossServerListCtrl", UIBaseCtrl)

local function CloseSelf(self)
    UIManager:GetInstance():DestroyWindow(UIWindowNames.UICrossServerList)
end

local function Close(self)
    UIManager:GetInstance():DestroyWindowByLayer(UILayer.Normal)
end

local function GetMaxServerId(self)
    return DataCenter.AccountManager:GetMaxServerId()
end

local function GetServerList(self)
    local maxServerId = self:GetMaxServerId()
    local list = {}
    for i = maxServerId,1,-1 do
        if i~=11 then
            local oneData = {}
            oneData.serverId = i
            table.insert(list,oneData)
        end
    end
    return list
end

UICrossServerListCtrl.CloseSelf = CloseSelf
UICrossServerListCtrl.Close = Close
UICrossServerListCtrl.GetServerList =GetServerList
UICrossServerListCtrl.GetMaxServerId = GetMaxServerId
return UICrossServerListCtrl