local UIDragonBattleResultCtrl = BaseClass("UIDragonBattleResultCtrl", UIBaseCtrl)
local Localization = CS.GameEntry.Localization
function UIDragonBattleResultCtrl:CloseSelf()
    UIManager:GetInstance():DestroyWindow(UIWindowNames.UIDragonBattleResult,{ anim = false})
    if LuaEntry.Player.serverType == ServerType.DRAGON_BATTLE_FIGHT_SERVER then
        EventManager:GetInstance():Broadcast(EventId.OnQuitDragonUI,UIQuitDragonType.Open)
        SFSNetwork.SendMessage(MsgDefines.ExitDragonServer)
    end
end

function UIDragonBattleResultCtrl:Close()
    UIManager:GetInstance():DestroyWindowByLayer(UILayer.Info)
end

return UIDragonBattleResultCtrl