local UIEquipmentAdvanceSuccessCtrl = BaseClass("UIEquipmentAdvanceSuccessCtrl", UIBaseCtrl)

local function CloseSelf(self)
    UIManager:GetInstance():DestroyWindow(UIWindowNames.UIEquipmentAdvanceSuccess)
end

local function GetPanelData(self, equipmentId)
    local result = {}
    result.preEquipmentId = equipmentId - 1
    result.curEquipmentId = equipmentId
    local attr = EquipmentUtil.GetEquipmentAttr(equipmentId)
    for k, v in ipairs(attr) do
        if v.isLocked == true then
            break
        end
        result.attr = v
    end
    return result
end

UIEquipmentAdvanceSuccessCtrl.CloseSelf = CloseSelf
UIEquipmentAdvanceSuccessCtrl.GetPanelData = GetPanelData

return UIEquipmentAdvanceSuccessCtrl