local FormationCamp = BaseClass("FormationCamp",UIBaseContainer)
local CampDetailItem = require "UI.UIFormation.UIFormationTableNew.Component.CampDetailItem"
local base = UIBaseContainer
local Localization = CS.GameEntry.Localization
local bg_path = ""
local des_txt_path ="desTxt"
local icon_path ="CampDetailItem"
local function OnCreate(self)
    base.OnCreate(self)
    self.bg = self:AddComponent(UIImage, bg_path)
    self.des_txt = self:AddComponent(UITextMeshProUGUIEx, des_txt_path)
    self.icon = self:AddComponent(CampDetailItem, icon_path)

end

local function OnDestroy(self)
    self.bg = nil
    self.des_txt =nil
    self.icon = nil
    base.OnDestroy(self)
end

local function OnEnable(self)
    base.OnEnable(self)
end

local function OnDisable(self)
    base.OnDisable(self)
end


local function RefreshData(self,index,data)
    if data~=nil then
        local list = {data}
        self.icon:InitData(list)
        local addNum = " <color=#27BE8C>" .. math.floor(data.addEffectNum).."%" .. "</color>"
        local str = Localization:GetString(data.dialog,data.num,addNum)
        self.des_txt:SetText(str)
    end
end

FormationCamp.OnCreate = OnCreate
FormationCamp.OnDestroy = OnDestroy
FormationCamp.RefreshData = RefreshData
FormationCamp.OnEnable = OnEnable
FormationCamp.OnDisable = OnDisable
return FormationCamp