local UIHeroEtoileList = BaseClass("UIHeroEtoileList", UIBaseContainer)
local base = UIBaseContainer
local UIHeroEtoile = require "UI.UIHero2.Common.UIHeroEtoile"

local etoile_path = "Etoile_%s"

local EtoileCount = 5

local function OnCreate(self)
    base.OnCreate(self)
    self:ComponentDefine()
    self:DataDefine()
end

local function OnDestroy(self)
    self:ComponentDestroy()
    self:DataDestroy()
    base.OnDestroy(self)
end

local function ComponentDefine(self)
    self.etoiles = {}
    for i = 1, EtoileCount do
        self.etoiles[i] = self:AddComponent(UIHeroEtoile, string.format(etoile_path, i))
    end
end

local function ComponentDestroy(self)

end

local function DataDefine(self)
    
end

local function DataDestroy(self)
    
end

local function OnEnable(self)
    base.OnEnable(self)
end

local function OnDisable(self)
    base.OnDisable(self)
end

local function SetRankId(self, rankId)
    if rankId == nil or rankId <= 0 then
        rankId = 1
    end
    local level = tonumber(GetTableData(TableName.HeroMilitaryRank, rankId, "level"))
    local stage = tonumber(GetTableData(TableName.HeroMilitaryRank, rankId, "stage"))
    self:SetLevelStage(level, stage)
end
local function SetRankIdSpecial(self,rankId)
    if rankId == nil or rankId <= 0 then
        rankId = 1
    end
    local level = tonumber(GetTableData(TableName.HeroMilitaryRank, rankId, "level"))
    local stage = tonumber(GetTableData(TableName.HeroMilitaryRank, rankId, "stage"))
    for i = 1, EtoileCount do
        local cornerCount
        if i < level then
            cornerCount = 5
        elseif i == level then
            cornerCount = stage
        else
            cornerCount = 0
        end
        if cornerCount>0 then
            self.etoiles[i]:SetActive(true)
            self.etoiles[i]:SetCornerCount(cornerCount)
        else
            self.etoiles[i]:SetActive(false)
        end
    end
    
end
local function SetLevelStage(self, level, stage)
    for i = 1, EtoileCount do
        local cornerCount
        if i < level then
            cornerCount = 5
        elseif i == level then
            cornerCount = stage
        else
            cornerCount = 0
        end
        self.etoiles[i]:SetCornerCount(cornerCount)
    end
end

UIHeroEtoileList.OnCreate = OnCreate
