    local num = 0
    local strArr = string.split(str,",")
    if #strArr>0 then
        num = toInt(strArr[1])
    end
    for i = 1, StarCount do
        if hideBg then
            self.star_bg_goes[i]:SetActive(i <= num)
        else
            self.star_bg_goes[i]:SetActive(true)
        end
        
        self.star_images[i]:SetActive(i <= num)
