local MainMiniMapDragonItem = BaseClass("MainMiniMapDragonItem",UIBaseContainer)
local base = UIBaseContainer
local icon_path = ""
local function OnCreate(self)
    base.OnCreate(self)
    self.icon = self:AddComponent(UIImage,icon_path)
end

local function ReInit(self,itemData,MapScale,MapDelta)
    self.icon:LoadSprite(itemData.icon)
    local size = {
        x = 20*itemData.scale,
        y = 20*itemData.scale
    }
    self:SetSizeDelta(size)
    self:SetAnchoredPositionXY((itemData.pos.x-MapDelta)*MapScale,(itemData.pos.y-MapDelta)*MapScale)
end

MainMiniMapDragonItem.OnCreate = OnCreate
MainMiniMapDragonItem.ReInit = ReInit
return MainMiniMapDragonItem