local AllianceItem = BaseClass("AllianceItem", UIBaseContainer)
local base = UIBaseContainer

local btn_path = "btn"
local red_dot_path = "RedDot"
local red_txt_path = "RedDot/DotText"
local alliance_left_btn_path = "alliance_left_btn"
local alliance_left_btn_img_path = "alliance_left_btn/alliance_left_btn_img"

local LeftBtnState =
{
    None = 0,--不显示
    AllianceHelp = 1,--联盟帮助
    JoinAlliance = 2,--加入联盟
}

function AllianceItem : OnCreate()
    base.OnCreate(self)
    self:ComponentDefine()
    self:DataDefine()
end

function AllianceItem : OnDestroy()
    self:ComponentDestroy()
    self:DataDestroy()
    base.OnDestroy(self)
end

function AllianceItem : OnEnable()
    base.OnEnable(self)
    self:Refresh()
end

function AllianceItem : OnDisable()
    base.OnDisable(self)
end

function AllianceItem : ComponentDefine()
    self.btn = self:AddComponent(UIButton, btn_path)
    self.btn:SetOnClick(function ()
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Main_Alliance)
        self:OnClickBtn()
    end)
    self.red_obj = self:AddComponent(UIBaseComponent,red_dot_path)
    self.red_txt = self:AddComponent(UITextMeshProUGUIEx,red_txt_path)
    self.alliance_left_btn = self:AddComponent(UIButton, alliance_left_btn_path)
    self.alliance_left_btn:SetOnClick(function()
        self:OnClickAllianceLeftBtn()
    end)
    self.alliance_left_btn_img = self:AddComponent(UIImage, alliance_left_btn_img_path)
end

function AllianceItem : ComponentDestroy()

end

function AllianceItem : DataDefine()
    self.left_btn_state = LeftBtnState.None
end

function AllianceItem : DataDestroy()

end

function AllianceItem : OnClickBtn()
    self.view.ctrl:OnBtnClick(UIMainFunctionInfo.Alliance)
end

function AllianceItem : Refresh()
    self:RefreshRedDot()
end
function AllianceItem : RefreshRedDot()
    local num = self.view.ctrl:GetShowRedNum(UIMainFunctionInfo.Alliance)
    if num>0 then
        self.red_obj:SetActive(true)
        if LuaEntry.Player:IsInAlliance() then
            self.red_txt:SetText(num)
            self.red_obj.rectTransform:Set_sizeDelta(32, 32)
            local helpNum = DataCenter.AllianceHelpDataManager:GetHelpNum()
            if helpNum > 0 then
                self.alliance_left_btn:SetActive(true)
                self.left_btn_state = LeftBtnState.AllianceHelp
                self.alliance_left_btn_img:LoadSprite(string.format(LoadPath.UIMain, "UIMain_btn_help"))
            else
                self.alliance_left_btn:SetActive(false)
                self.left_btn_state = LeftBtnState.None
            end
        else
            self.red_obj.rectTransform:Set_sizeDelta(24, 24)

            self.red_txt:SetText("")
            if DataCenter.SecondConfirmManager:GetCanShowSecondConfirm(NoSecondConfirmType.JoinAlliance) then
                self.alliance_left_btn:SetActive(true)
                self.left_btn_state = LeftBtnState.JoinAlliance
                self.alliance_left_btn_img:LoadSprite(string.format(LoadPath.UIMain, "UIMain_btn_joinAlliance"))
            else
                self.alliance_left_btn:SetActive(false)
                self.left_btn_state = LeftBtnState.None
            end
        end
    else
        self.red_obj:SetActive(false)
        self.alliance_left_btn:SetActive(false)
        self.left_btn_state = LeftBtnState.None
    end
end

function AllianceItem :OnClickAllianceHelpBtn()
    local curTime = UITimeManager:GetInstance():GetServerTime()
    DataCenter.AllianceHelpDataManager:SetHelpNum(0)
    SFSNetwork.SendMessage(MsgDefines.AlHelpAll,math.floor(curTime))
    local iconPath2 = DataCenter.ItemTemplateManager:GetAllianceItemIconPath(RewardType.ALLIANCE_DONATE)
    UIUtil.DoFly(RewardType.GOODS, 3, iconPath2, self.alliance_left_btn.transform.position, UIUtil.GetUIMainSavePos(UIMainSavePosType.Alliance), 
    50, 50)
end

function AllianceItem:OnClickAllianceLeftBtn()
    if self.left_btn_state == LeftBtnState.AllianceHelp then
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Main_AlHelp)
        self:OnClickAllianceHelpBtn()
    elseif self.left_btn_state == LeftBtnState.JoinAlliance then
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Main_Alliance)
        UIManager:GetInstance():OpenWindow(UIWindowNames.UIAllianceIntro, NormalBlurPanelAnim)
    end
end


return AllianceItem
