local MailItem = BaseClass("MailItem", UIBaseContainer)
local base = UIBaseContainer

local mailBtn_path = ""
--local mailTxt_path = "mailTxt"
local red_dot_path = "RedDot"
local red_txt_path = "RedDot/DotText"
function MailItem : OnCreate()
    base.OnCreate(self)
    self:ComponentDefine()
    self:DataDefine()
end

function MailItem : OnDestroy()
    self:ComponentDestroy()
    self:DataDestroy()
    base.OnDestroy(self)
end

function MailItem : OnEnable()
    base.OnEnable(self)
end

function MailItem : OnDisable()
    base.OnDisable(self)
end

function MailItem : ComponentDefine()
    self.MailBtn = self:AddComponent(UIButton, mailBtn_path)
    self.MailBtn:SetOnClick(function ()
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Main_Mail)
        self:OnClickMailBtn()
    end)
    self.red_obj = self:AddComponent(UIBaseComponent,red_dot_path)
    self.red_txt = self:AddComponent(UITextMeshProUGUIEx,red_txt_path)
end

function MailItem : ComponentDestroy()

end

function MailItem : DataDefine()

end

function MailItem : DataDestroy()

end

function MailItem : OnClickMailBtn()
    self.view.ctrl:OnBtnClick(UIMainFunctionInfo.Mail)
end

function MailItem : Refresh()
    self:RefreshRedDot()
end
function MailItem : RefreshRedDot()
    local num = self.view.ctrl:GetShowRedNum(UIMainFunctionInfo.Mail)
    if num>0 then
        self.red_obj:SetActive(true)
        self.red_txt:SetText(num)
    else
        self.red_obj:SetActive(false)
    end
end

return MailItem
