function UIMasteryPageNameCtrl:CheckName(value)
    local type = CheckNameType.None
    local len = #value

    if len< MIN_NAME_CHAR then
        type = CheckNameType.MinNameChar
    elseif len>MAX_NAME_CHAR then
        type = CheckNameType.MaxNameChar
    end
    return type
end

