local UIPuzzleMonsterRankCtrl = BaseClass("UIPuzzleMonsterRankCtrl", UIBaseCtrl)

function UIPuzzleMonsterRankCtrl:CloseSelf()
    UIManager.Instance:DestroyWindow(UIWindowNames.UIPuzzleMonsterRank)
end

function UIPuzzleMonsterRankCtrl:GetBossRankData(uuid, puzzleType)
    local data = nil
    if puzzleType == PuzzleMonsterType.Activity then
        data = DataCenter.ActivityPuzzleDataManager:GetBossRankDataByUuid(uuid)
    elseif puzzleType == PuzzleMonsterType.Radar then
        data = DataCenter.RadarBossManager:GetBossRankDataByUuid(uuid)
    end
    if data ~= nil then
        return data
    end
    return {}
end

function UIPuzzleMonsterRankCtrl:GetActivityRewardList(uuid, puzzleType)
    local list = nil
    if puzzleType == PuzzleMonsterType.Activity then
        list = DataCenter.ActivityPuzzleDataManager:GetBossRankReward(uuid)
    elseif puzzleType == PuzzleMonsterType.Radar then
        list = DataCenter.RadarBossManager:GetBossRankReward(uuid)
    end
    if list ~= nil then
        return list
    end
    return {}
end

return UIPuzzleMonsterRankCtrl