local UIRuinsHistoryRecordCtrl = BaseClass("UIRuinsHistoryRecordCtrl", UIBaseCtrl)

local function CloseSelf(self)
    UIManager:GetInstance():DestroyWindow(UIWindowNames.UIRuinsHistoryRecord)
end

local function GetRecordList(self)
    local showList = {}
    local tempList = DataCenter.WorldAllianceCityRecordManager:GetRecordList()
    if tempList~=nil then
        for i=1,#tempList do
            local temp = DataCenter.WorldAllianceCityRecordManager:GetRecordDataByUuid(tempList[i].uuid)
            if temp~=nil then
                table.insert(showList,temp)
            end
        end
       
    end
    return showList
end
UIRuinsHistoryRecordCtrl.CloseSelf = CloseSelf
UIRuinsHistoryRecordCtrl.GetRecordList = GetRecordList
return UIRuinsHistoryRecordCtrl