local SearchElement = BaseClass("SearchElement", UIBaseContainer)
local base = UIBaseContainer
local Localization = CS.GameEntry.Localization

local btn_path = ""
local btn_txt_path = "Btn_Text"
local choose_path = "choose"
local function OnCreate(self)
    base.OnCreate(self)
    self.btn = self:AddComponent(UIButton, btn_path)
    self.btn_text = self:AddComponent(UITextMeshProUGUIEx, btn_txt_path)
    self.btn:SetOnClick(function ()
        self:OnSearchClick()
    end)
    self.select = self:AddComponent(UIBaseContainer,choose_path)
end

local function SetData(self,searchType)
    self.select:SetActive(false)
    self.searchType = searchType
    if self.searchType == UISearchType.Monster then
        self.btn_text:SetLocalText(GameDialogDefine.SEARCH_MONSTER)
    elseif self.searchType == UISearchType.Boss then
        self.btn_text:SetLocalText(300602)
    elseif self.searchType == UISearchType.Food then
        self.btn_text:SetLocalText(442000)
    elseif self.searchType == UISearchType.Wood then
        self.btn_text:SetLocalText(442001)
    elseif self.searchType == UISearchType.Electricity then
        self.btn_text:SetLocalText(442003)
    elseif self.searchType == UISearchType.Metal then
        self.btn_text:SetLocalText(442002)
    elseif self.searchType == UISearchType.Money then
        self.btn_text:SetLocalText(441999)
    end
end
local function OnSelectClick(self,selectType)
    self.select:SetActive(self.searchType == selectType)
end

local function OnSearchClick(self)
    self.view:OnSearchClick(self.searchType)
end

SearchElement.OnCreate = OnCreate
SearchElement.SetData = SetData
SearchElement.OnSelectClick = OnSelectClick
SearchElement.OnSearchClick = OnSearchClick
return SearchElement