local RewardItem = require "UI.UIWorldPoint.Component.WorldPointRewardItem"
local SeasonGetRewardObj = BaseClass("SeasonGetRewardObj", UIBaseContainer)
local base = UIBaseContainer
local Localization = CS.GameEntry.Localization

-- 创建
function SeasonGetRewardObj:OnCreate()
    base.OnCreate(self)
    self.title = self:AddComponent(UIText,"title/titleTxt")
    self.title:SetText(Localization:GetString("104191"))
    self.Text_reward = self:AddComponent(UIText,"title/Text_num/Text_reward")
    self.Text_reward:SetText(Localization:GetString("370101",""))
    self.Text_num = self:AddComponent(UIText,"title/Text_num")
    self._get_btn = self:AddComponent(UIButton,"getBtn")
    self._get_btn:SetOnClick(function()
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Button)
        self:ReceiveSelfReward()
    end)
    self._get_txt = self:AddComponent(UIText,"getBtn/getTxt")
    self._get_txt:SetText(Localization:GetString("110317"))
    self._finish_txt = self:AddComponent(UIText,"finishTxt")
    self._finish_txt:SetText(Localization:GetString("170003"))
    self.reward_layout = self:AddComponent(UIBaseContainer,"ScrollView/Viewport/Content")
end

-- 销毁
function SeasonGetRewardObj:OnDestroy()
    self:ClearItemCell()
    base.OnDestroy(self)
end

-- 显示
function SeasonGetRewardObj:OnEnable()
    base.OnEnable(self)
end

-- 隐藏
function SeasonGetRewardObj:OnDisable()
    base.OnDisable(self)
end

function SeasonGetRewardObj:RefreshList(data)
    self:ClearItemCell()
    local count = data.diamonds
    if data.selfStatus ==1 then
        self._get_btn:SetActive(true)
        self._finish_txt:SetActive(false)
    else
        self._get_btn:SetActive(false)
        self._finish_txt:SetActive(true)
    end
    self.Text_num:SetText(string.GetFormattedSeperatorNum(math.floor(count)))
    local list = data.rewardList
    if #list >0 then
        local num =0
        for i = 1, table.length(list) do
            --复制基础prefab，每次循环创建一次
            num = num+1
            self.rewardModel[i] = self:GameObjectInstantiateAsync(UIAssets.WorldPointRewardItem, function(request)
                if request.isError then
                    return
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.reward_layout.transform)
                go.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local cell = self.reward_layout:AddComponent(RewardItem,nameStr)
                cell:RefreshData(list[i])
            end)
        end
    end
end
function SeasonGetRewardObj:ClearItemCell()
    self.reward_layout:RemoveComponents(RewardItem)
    if self.rewardModel~=nil then
        for k,v in pairs(self.rewardModel) do
            if v ~= nil then
                self:GameObjectDestroy(v)
            end
        end
    end
    self.rewardModel ={}
end

function SeasonGetRewardObj:ReceiveSelfReward()
    local giftId = self.view.ctrl:GetSelfPackageId()
    SFSNetwork.SendMessage(MsgDefines.SeasonBalanceReceivePackage,giftId)
    self.view.ctrl:CloseSelf()
end

return SeasonGetRewardObj