local UISeasonAllianceRewardDisplayMainCtrl = BaseClass("UISeasonAllianceRewardDisplayMainCtrl", UIBaseCtrl)
local Data = CS.GameEntry.Data
local Localization = CS.GameEntry.Localization
function UISeasonAllianceRewardDisplayMainCtrl:CloseSelf()
    UIManager:GetInstance():DestroyWindow(UIWindowNames.UISeasonAllianceRewardDisplayMain)
end

function UISeasonAllianceRewardDisplayMainCtrl:Close()
    UIManager:GetInstance():DestroyWindowByLayer(UILayer.Normal)
end

function UISeasonAllianceRewardDisplayMainCtrl:GetRewardAllianceData(stageType)
    local showList = {}
    local dataList = DataCenter.DesertDataManager:GetRewardListByStage(stageType)
    if dataList~=nil and #dataList>0 then
        for i = 1,#dataList do
            local data = dataList[i]
            local temList = data.allianceList
            table.insertto(showList,temList)
        end
    end

    return showList
end

return UISeasonAllianceRewardDisplayMainCtrl