local UIWeekThemeItem = BaseClass("UIWeekThemeItem", UIBaseContainer)
local base = UIBaseContainer
local Localization = CS.GameEntry.Localization

local this_path = ""
local name_path = "Name"
local icon_path = "Icon"

local function OnCreate(self)
    base.OnCreate(self)
    self:DataDefine()
    self:ComponentDefine()
end

local function OnDestroy(self)
    self:ComponentDestroy()
    self:DataDestroy()
    base.OnDestroy(self)
end

local function ComponentDefine(self)
    self.btn = self:AddComponent(UIButton, this_path)
    self.btn:SetOnClick(function()
        self:OnClick()
    end)
    self.name_text = self:AddComponent(UIText, name_path)
    self.icon_image = self:AddComponent(UIImage, icon_path)
end

local function ComponentDestroy(self)
    self.btn = nil
    self.name_text = nil
    self.icon_image = nil
end

local function DataDefine(self)
    self.data = nil
end

local function DataDestroy(self)
    self.data = nil
end

local function OnEnable(self)
    base.OnEnable(self)
end

local function OnDisable(self)
    base.OnDisable(self)
end

local function OnAddListener(self)
    base.OnAddListener(self)
end

local function OnRemoveListener(self)
    base.OnRemoveListener(self)
end

local function SetData(self, data)
    self.data = data
    if self.data.type == 1 then
        local line = LocalController:instance():getLine(TableName.ActivityPanel, data.id)
        self.name_text:SetLocalText(line and line.name or "")
    elseif self.data.type == 2 then
        self.name_text:SetLocalText(data.id)
    end
    self.icon_image:LoadSprite(string.format(LoadPath.UIGlory, data.icon))
end

local function OnClick(self)
    if self.data.type == 1 then
        local window = UIManager:GetInstance():GetWindow(UIWindowNames.UIActivityCenterTable)
        if window then
            window.View:GotoActivityByExternal(self.data.id)
        else
            UIManager:GetInstance():OpenWindow(UIWindowNames.UIActivityCenterTable, { anim = true, UIMainAnim = UIMainAnimType.AllHide }, self.data.id)
        end
    elseif self.data.type == 2 then
        UIManager:GetInstance():OpenWindow(UIWindowNames.UIGloryBattle, { anim = true, UIMainAnim = UIMainAnimType.AllHide })
    end
end

UIWeekThemeItem.OnCreate= OnCreate
UIWeekThemeItem.OnDestroy = OnDestroy
