UIWeekThemeItem.ComponentDestroy = ComponentDestroy
UIWeekThemeItem.DataDefine = DataDefine
UIWeekThemeItem.DataDestroy = DataDestroy
UIWeekThemeItem.OnEnable = OnEnable
UIWeekThemeItem.OnDisable = OnDisable
UIWeekThemeItem.OnAddListener = OnAddListener
UIWeekThemeItem.OnRemoveListener = OnRemoveListener

UIWeekThemeItem.SetData = SetData
UIWeekThemeItem.OnClick = OnClick


return UIWeekThemeItem