local UITalentInfoCell = BaseClass("UITalentInfoCell",UIBaseContainer)
local base = UIBaseContainer
local UITalentIcon =  require "UI.UITalent.UITalentInfo.Component.UITalentIcon"

local content_path ="mainContent/Content"
local Localization = CS.GameEntry.Localization
local function OnCreate(self)
    base.OnCreate(self)
    self.content = self:AddComponent(UIBaseContainer,content_path)
    self.model = {}
end

local function SetItemShow(self, data)
    self.data = data
    self:SetAllCellDestroy()
    local list = self.data.talents
    if list ~= nil then
        for i = 1, table.length(list) do
            self.model[i] = self:GameObjectInstantiateAsync(UIAssets.UITalentIcon, function(request)
                if request.isError then
                    return
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.content.transform)
                go.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local cell = self.content:AddComponent(UITalentIcon, nameStr)
                cell:SetData(list[i])
            end)
        end
    end

end


local function OnDestroy(self)
    base.OnDestroy(self)
end

local function SetAllCellDestroy(self)
    self.content:RemoveComponents(UITalentIcon)
    if self.model~=nil then
        for k,v in pairs(self.model) do
            if v ~= nil then
                self:GameObjectDestroy(v)
            end
        end
    end
    self.model ={}
end


UITalentInfoCell.OnCreate = OnCreate
UITalentInfoCell.SetItemShow = SetItemShow
UITalentInfoCell.OnDestroy =OnDestroy
UITalentInfoCell.SetAllCellDestroy =SetAllCellDestroy
return UITalentInfoCell