local ThroneWarItem = require "UI.UIAssistanceThrone.Component.ThroneWarItem"
local AllianceWarPlayerItem = require "UI.UIAlliance.UIAllianceWarDetail.Component.AllianceWarPlayerItem"
local UIThroneDetailView = BaseClass("UIThroneDetailView",UIBaseView)
local base = UIBaseView
local Localization = CS.GameEntry.Localization

local txt_title_path ="UICommonPopUpTitle/bg_mid/titleText"
local close_btn_path = "UICommonPopUpTitle/bg_mid/CloseBtn"
local content_path = "ImgBg/ScrollView/Viewport/Content"
local return_btn_path = "UICommonPopUpTitle/panel"
local war_item_obj_path = "ImgBg/ThroneWarItem"
local desTxt_path = "ImgBg/desObj/desTxt"

local function OnCreate(self)
    base.OnCreate(self)
    self.data = self:GetUserData()
    self.title = self:AddComponent(UITextMeshProUGUIEx,txt_title_path)
    self.title:SetLocalText(141024)
    self.close_btn = self:AddComponent(UIButton, close_btn_path)
    self.close_btn:SetOnClick(function()
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Button)
        self.ctrl:OnCloseClick()
    end)
    self.return_btn = self:AddComponent(UIButton, return_btn_path)
    self.return_btn:SetOnClick(function()
        SoundUtil.PlayEffect(SoundAssets.Music_Effect_Button)
        self.ctrl:OnCloseClick()
    end)
    self.war_item_obj = self:AddComponent(ThroneWarItem,war_item_obj_path)
    self.content = self:AddComponent(UIBaseContainer,content_path)
    
    self.desTxt = self:AddComponent(UIText,desTxt_path)
    self.desTxt:SetLocalText(395415)
end

local function OnDestroy(self)
    self:SetAllCellDestroy()
    base.OnDestroy(self)
end

local function OnEnable(self)
    base.OnEnable(self)
    self:OnRefresh()
end

local function OnDisable(self)
    base.OnDisable(self)
end

local function OnRefresh(self)
    self:SetAllCellDestroy()
    self.war_item_obj:RefreshData(self.data)
    self.ctrl:SetMemberData(self.data.memberList,self.data.uuid,self.data.leaderMarchUuid)
    local list = self.ctrl:GetPlayerIdList()
    if list~=nil then
        for i = 1, table.length(list) do
            --复制基础prefab，每次循环创建一次
            self.marchBottom[list[i]] = self:GameObjectInstantiateAsync(UIAssets.AllianceWarPlayerItem, function(request)
                if request.isError then
                    return
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.content.transform)
                go.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local cell = self.content:AddComponent(AllianceWarPlayerItem,nameStr)
                cell:SetUuid(list[i])
                cell:RefreshData(false)
            end)
        end
    end
end

local function SetAllCellDestroy(self)
    self.content:RemoveComponents(AllianceWarPlayerItem)
    if self.marchBottom and next(self.marchBottom) then
        for k,v in pairs(self.marchBottom) do
            if v ~= nil then
                self:GameObjectDestroy(v)
            end
        end
    end
    self.marchBottom ={}
end

local function OnAddListener(self)
    base.OnAddListener(self)
    self:AddUIListener(EventId.UpdateMarchItem,self.RefreshFirstMarch)
end

local function OnRemoveListener(self)
    base.OnRemoveListener(self)
    self:RemoveUIListener(EventId.UpdateMarchItem,self.RefreshFirstMarch)
end

local function OnCancelClick(self,uuid)
    if self.data~=nil then
        self.ctrl:OnCancelClick(self.data.bUuid,uuid)
    end
    self.ctrl:OnCloseClick()
end

local function RefreshFirstMarch(self)
    self.war_item_obj:OnMarchRefresh()
end
UIThroneDetailView.OnCreate = OnCreate
UIThroneDetailView.OnDestroy = OnDestroy
UIThroneDetailView.OnRefresh = OnRefresh
UIThroneDetailView.OnEnable = OnEnable
UIThroneDetailView.OnDisable = OnDisable
UIThroneDetailView.OnAddListener = OnAddListener
UIThroneDetailView.OnRemoveListener = OnRemoveListener
UIThroneDetailView.SetAllCellDestroy = SetAllCellDestroy
UIThroneDetailView.OnCancelClick =OnCancelClick
UIThroneDetailView.RefreshFirstMarch =RefreshFirstMarch
return UIThroneDetailView