local RewardItem = require "UI.UIWorldPoint.Component.WorldPointRewardItem"
local UIWorldBossAtkTimeRewardItem = BaseClass("UIWorldBossAtkTimeRewardItem",UIBaseContainer)
local base = UIBaseContainer

local rank_path = "rewardBg/TitleBg/Title"
local content_path ="rewardBg/Content"
local Localization = CS.GameEntry.Localization
local function OnCreate(self)
    base.OnCreate(self)
    self.rank = self:AddComponent(UIText,rank_path)
    self.content = self:AddComponent(UIBaseContainer,content_path)
    self.model = {}
end

local function SetItemShow(self, data)
    self.data = data
    if data.rankStr~="" then
        self.rank:SetText(Localization:GetString("302242",data.rankStr))
    else
        self.flag:SetActive(false)
        self.rank:SetText("+999")
    end
    self:SetAllCellDestroy()
    local list = self.data.rewardStr
    if list~=nil then
        for i = 1, table.length(list) do
            self.model[i] = self:GameObjectInstantiateAsync(UIAssets.WorldPointRewardItem, function(request)
                if request.isError then
                    return
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.content.transform)
                go.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local cell = self.content:AddComponent(RewardItem,nameStr)
                cell:RefreshData(list[i])
            end)
        end
    end

end


local function OnDestroy(self)
    base.OnDestroy(self)
end

local function SetAllCellDestroy(self)
    self.content:RemoveComponents(RewardItem)
    if self.model~=nil then
        for k,v in pairs(self.model) do
            if v ~= nil then
                self:GameObjectDestroy(v)
            end
        end
    end
    self.model ={}
end


UIWorldBossAtkTimeRewardItem.OnCreate = OnCreate
UIWorldBossAtkTimeRewardItem.SetItemShow = SetItemShow
UIWorldBossAtkTimeRewardItem.OnDestroy =OnDestroy
UIWorldBossAtkTimeRewardItem.SetAllCellDestroy =SetAllCellDestroy
return UIWorldBossAtkTimeRewardItem