local RewardItem = require "UI.UIWorldPoint.Component.WorldPointRewardItem"
local WorldMonsterReward = BaseClass("WorldMonsterReward", UIBaseContainer)
local base = UIBaseContainer
local Localization = CS.GameEntry.Localization
local content_path = "ScrollView/Viewport/Content"
local time_txt_path = "time/timeLabel"
local tips_txt_path = "tips/commendText"
local time_obj_path = "time"
-- 创建
local function OnCreate(self)
    base.OnCreate(self)
    self:ComponentDefine()
    self:DataDefine()
end

-- 销毁
local function OnDestroy(self)
    self:ComponentDestroy()
    self:DataDestroy()
    base.OnDestroy(self)
end

-- 显示
local function OnEnable(self)
    base.OnEnable(self)
end

-- 隐藏
local function OnDisable(self)
    self:DeleteTimer()
    base.OnDisable(self)
end


--控件的定义
local function ComponentDefine(self)
    self.content = self:AddComponent(UIBaseContainer,content_path)
    self.time_txt = self:AddComponent(UITextMeshProUGUIEx,time_txt_path)
    self.tips_txt = self:AddComponent(UITextMeshProUGUIEx,tips_txt_path)
    self.time_obj = self:AddComponent(UIBaseContainer,time_obj_path)
end

--控件的销毁
local function ComponentDestroy(self)
    self.btn = nil
    self.btnImage = nil
end

--变量的定义
local function DataDefine(self)
    self.data = nil
    self.timer_action = function(temp)
        self:RefreshTime()
    end
end

--变量的销毁
local function DataDestroy(self)
    self.data = nil
end

local function SetAllCellDestroy(self)
    self.content:RemoveComponents(RewardItem)
    if self.model~=nil then
        for k,v in pairs(self.model) do
            if v ~= nil then
                self:GameObjectDestroy(v)
            end
        end
    end
    self.model ={}
end
local function RefreshData(self,param)
    self.data = param
    self.tips_txt:SetLocalText(104191) 
    if self.data.isIncity == false then
        self.time_obj:SetActive(true)
        self:AddTimer()
    else
        self.time_obj:SetActive(false)
    end
    self:SetAllCellDestroy()
    local list = self.data.rewardStr
    if list~=nil then
        local num =0
        for i = 1, table.length(list) do
            --复制基础prefab，每次循环创建一次
            num = num+1
            self.model[i] = self:GameObjectInstantiateAsync(UIAssets.WorldPointRewardItem, function(request)
                if request.isError then
                    return
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.content.transform)
                go.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local cell = self.content:AddComponent(RewardItem,nameStr)
                cell:RefreshData(list[i])
            end)
        end
        if self.data.exp ~= nil then
            self.model[num+1] = self:GameObjectInstantiateAsync(UIAssets.WorldPointRewardItem, function(request)
                if request.isError then
                    return
                end
                local go = request.gameObject;
                go.gameObject:SetActive(true)
                go.transform:SetParent(self.content.transform)
                go.transform:Set_localScale(ResetScale.x, ResetScale.y, ResetScale.z)
                local nameStr = tostring(NameCount)
                go.name = nameStr
                NameCount = NameCount + 1
                local oneData = {}
                oneData.count = self.data.exp
                oneData.itemColor = DataCenter.ItemTemplateManager:GetToolBgByColor(ItemColor.GREEN)
                oneData.iconName = string.format(LoadPath.ItemPath, "item230001")
                oneData.itemFlag = ""
                oneData.rewardType = RewardType.EXP
                oneData.itemName = Localization:GetString("100083")
                oneData.itemDesc = Localization:GetString("302010",string.GetFormattedSeperatorNum(self.data.exp))
                oneData.isLocal = true
                local cell = self.content:AddComponent(RewardItem,nameStr)
                cell:RefreshData(oneData)
            end)
        end
    end
end

local function DeleteTimer(self)
    if self.timer ~= nil then
        self.timer:Stop()
        self.timer = nil
    end
end

local function AddTimer(self)
    if self.timer == nil then
        self.timer = TimerManager:GetInstance():GetTimer(1, self.timer_action , self, false,false,false)
    end

    self.timer:Start()
end

local function RefreshTime(self)
    local curTime = UITimeManager:GetInstance():GetServerTime()
    local deltaTime = self.data.refreshTime - curTime
    if self.data~=nil and deltaTime>0 then
        self.time_txt:SetText(UITimeManager:GetInstance():MilliSecondToFmtString(deltaTime))
    else
        self.time_txt:SetText("")
    end
end
WorldMonsterReward.OnCreate = OnCreate
WorldMonsterReward.OnDestroy = OnDestroy
WorldMonsterReward.OnEnable = OnEnable
WorldMonsterReward.OnDisable = OnDisable
WorldMonsterReward.ComponentDefine = ComponentDefine
WorldMonsterReward.ComponentDestroy = ComponentDestroy
WorldMonsterReward.DataDefine = DataDefine
WorldMonsterReward.DataDestroy = DataDestroy
WorldMonsterReward.AddTimer = AddTimer
WorldMonsterReward.DeleteTimer = DeleteTimer
WorldMonsterReward.RefreshTime = RefreshTime
WorldMonsterReward.RefreshData = RefreshData
WorldMonsterReward.SetAllCellDestroy = SetAllCellDestroy
return WorldMonsterReward